import React, { useState } from 'react';

const Count = () => {
    const [count, setCount] = useState(0);
    const [inputValue, setInputValue] = useState(''); 

    const fetchCount = async () => {
        try {
            const resp = await fetch('http://localhost:8081/count');
            if (!resp.ok) {
                throw new Error('Нет соединения');
            }
            const data = await resp.text();
            setCount(data);
        } catch (error) {
            setCount('Нет соединения с сервером');
        }
    };

    const incrementCount = async () => {
        try {
            const resp1 = await fetch('http://localhost:8083/count', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                },
                body: new URLSearchParams({ count: inputValue }), 
            });
            if (resp1.ok == false){
                throw new Error('Не отправились данные')
            }
            setInputValue(''); 

        } catch (error) {
            console.error('Не удалось отправить данные', error);
        }
    };

    return (
        <div>
            <h2>Count Service</h2>
            <button onClick={fetchCount}>Счётчик</button>
            <input 
                type="number" 
                value={inputValue} 
                onChange={(e) => setInputValue(e.target.value)} 
                placeholder="Введите число" 
            />
            <button onClick={incrementCount}>Увеличить счётик</button>
            <p>Счётчик: {count}</p>
        </div>
    );
};

export default Count;
