import React, { useState } from 'react';

const User = () => {
    const [name, setName] = useState('');
    const [greeting, setGreeting] = useState('');

    const fetchUserGreeting = async () => {
        try {
            const resp = await fetch(`http://localhost:8083/api/user?name=${name}`);
            if (!resp.ok) {
                throw new Error('Неправильный ответ');
            }
            const data = await resp.text();
            setGreeting(data);
        } catch (error) {
            console.error('Ошибка', error);
            setGreeting('Введите Ваше имя');
        }
    };

    return (
        <div>
            <h2>Знакомство</h2>
            <input
                type="text"
                placeholder="Введите своё имя"
                value={name}
                onChange={(e) => setName(e.target.value)}
            />
            <button onClick={fetchUserGreeting}>Поприветствовать</button>
            <p>Сообщение: {greeting}</p>
        </div>
    );
};

export default User;
